import React from "react";
import { ChatState } from "../Context/ChatProvider";
import { Box, IconButton, Text } from "@chakra-ui/react";

const SingleChat = ({ fetchAgain, setFetchAgain }) => {
  const { user, selectedChat, setSelectedChat } = ChatState();

  //   handler to sehow the selected chat
  const handleChatClick = (chat) => {
    setSelectedChat(chat);
  };

  return (
    <>
      {selectedChat ? (
        <>
          <Text
            fontSize={{ base: "28px", md: "30px" }}
            pb={3}
            px={2}
            w={"100%"}
            fontFamily={"Helvetica"}
            display={"flex"}
            justifyContent={{ base: "space-between" }}
            alignItems={"center"}
          >
            <IconButton
              display={{ base: "flex", md: "none" }}
              icon={<i className="fa-solid fa-arrow-left" />}
              onClick={() => setSelectedChat("")}
            />
            {!selectedChat.isGroupChat ? (
              <>
                {user._id !== selectedChat.users[0]._id ? (
                  <Text>
                    {selectedChat.users[0].name}
                    <br />
                    <Text fontSize={"xs"}>{selectedChat.users[0].email}</Text>
                  </Text>
                ) : (
                  <Text>
                    {selectedChat.users[1].name}
                    <br />
                    <Text fontSize={"xs"}>{selectedChat.users[1].email}</Text>
                  </Text>
                )}
                <Text fontSize={"xs"}>{selectedChat.users[0].email}</Text>
              </>
            ) : (
              <Text>{selectedChat.chatName.toUpperCase()}</Text>
            )}
          </Text>
        </>
      ) : (
        <Box
          display={"flex"}
          alignItems={"center"}
          h={"100%"}
          justifyContent={"center"}
          flexDir={"column"}
          paddingBottom={3}
          fontFamily={"Helvetica"}
          fontSize={"3xl"}
        >
          Please select a chat to start messaging...
        </Box>
      )}
    </>
  );
};

export default SingleChat;
