export const getSender = (loggedUser, users) => {
  // First, check if we have all required data
  if (!loggedUser || !users || !Array.isArray(users) || users.length < 2) {
    return "";
  }

  console.log("user1 id:", users[0]._id);
  console.log("user2 id:", users[1]._id);
  

  // Make sure both users exist and have required properties
  const user1 = users[0];
  const user2 = users[1];

  if (!user1?._id || !user2?._id || !user1?.name || !user2?.name) {
    return "";
  }

  // Return the appropriate user's name
  return users[0]._id === loggedUser._id ? users[1].name : users[0].name;
};
