import React, { useEffect, useRef, useState } from "react";
import { ChatState } from "../Context/ChatProvider";
import axios from "axios";
import {
  AlertDialog,
  AlertDialogBody,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogOverlay,
  Box,
  Button,
  Spinner,
  Stack,
  Text,
  useToast,
} from "@chakra-ui/react";
import { AddIcon, CloseIcon } from "@chakra-ui/icons";
import ChatLoading from "./ChatLoading";
import { getSender } from "../Config/ChatLogics";
import GroupChatModal from "./Miscellaneous/GroupChatModal";

const MyChats = () => {
  // const [loggedUser, setLoggedUser] = useState();
  const [loggedUser, setLoggedUser] = useState(
    JSON.parse(localStorage.getItem("userInfo"))
  );
  const { selectedChat, setSelectedChat, user, chats, setChats } = ChatState();

  const [isLoading, setIsLoading] = useState(true);

  // Deleting the chat
  const [isDeleteAlertOpen, setIsDeleteAlertOpen] = useState(false);
  const [chatToDelete, setChatToDelete] = useState(null);
  const cancelRef = useRef();

  // NEW: Add this state for backup
  const [deletedChatBackup, setDeletedChatBackup] = useState(null);

  const toast = useToast();

  const fetchChats = async () => {
    // CHANGE : Added early return for better performance
    if (!user?.token) return;

    // setIsLoading(true); // Changed
    try {
      // Changed
      if (!user || !user.token) {
        console.log("User not found or token missing");
        return;
      }
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await axios.get("/api/chat", config);

      // console.log("Fetched chats:", data); // Debug log
      // console.log(
      //   "Chat IDs:",
      //   data.map((chat) => chat._id)
      // );
      setChats(data);
    } catch (error) {
      console.error("Error fetching chats:", error);
      toast({
        title: "Error Occured!",
        description: "Failed to Load the chats",
        status: "error",
        duration: 3000,
        isClosable: true,
        position: "top-left",
      });
    } finally {
      // CHANGED : Reset loading state
      // setIsLoading(false);
    }
  };

  // Delete handler with new alert dialog box
  const handleDeleteClick = (chat) => {
    setChatToDelete(chat);
    setIsDeleteAlertOpen(true);
  };

  // NEW: Add this function for handling undo
  const handleUndoDelete = () => {
    if (!deletedChatBackup) return;

    // Restore the chat to its original position
    const newChats = [...chats];
    newChats.splice(deletedChatBackup.index, 0, deletedChatBackup.chat);
    setChats(newChats);

    // Show success toast
    toast({
      title: "Chat Restored",
      status: "success",
      duration: 3000,
      isClosable: true,
      position: "top",
    });

    // Clear the backup
    setDeletedChatBackup(null);
  };

  // CHANGED: Updated delete confirmation handler
  const handleDeleteConfirm = async () => {
    if (!chatToDelete) return;

    // Store chat info before deletion
    const chatToDeleteId = chatToDelete._id; // Store ID separately

    // Remove from UI immediately
    setChats(chats.filter((c) => c._id !== chatToDelete._id));

    if (selectedChat?._id === chatToDelete._id) {
      setSelectedChat(null);
    }

    // Backup for undo
    // NEW: Backup the chat and its index before deletion
    const chatBackup = chatToDelete;
    const chatIndex = chats.findIndex((c) => c._id === chatToDelete._id);
    setDeletedChatBackup({ chat: chatBackup, index: chatIndex });

    // NEW: Show toast with undo button
    toast({
      title: "Chat Deleted",
      description: (
        <Box>
          <Text mb={2}>Chat has been deleted</Text>
          <Button size="sm" colorScheme="blue" onClick={handleUndoDelete}>
            UNDO
          </Button>
        </Box>
      ),
      status: "success",
      duration: 5000,
      isClosable: true,
      position: "top",
    });

    // NEW: Delayed backend deletion
    setTimeout(async () => {
      // Only delete if not undone (deletedChatBackup still exists)
      if (deletedChatBackup) {
        try {
          const config = {
            headers: {
              
              Authorization: `Bearer ${user.token}`,
            },
          };

          // Use stored ID instead of relying on backup
          await axios.delete("/api/chat/delete", {
            headers: config.headers,
            data: { chatId: chatToDeleteId },
          });

          console.log("Chat deleted successfully"); // Debug log
          setDeletedChatBackup(null);
        } catch (error) {
          // If backend deletion fails, restore the chat
          handleUndoDelete();
          toast({
            title: "Error Deleting Chat",
            description:
              error.response?.data?.message || "Something went wrong",
            status: "error",
            duration: 3000,
            isClosable: true,
            position: "top",
          });
        }
      }
    }, 5000); // 5 seconds delay

    setIsDeleteAlertOpen(false);
    setChatToDelete(null);

    // Cleanup timeout
    // return () => clearTimeout(timeoutId);
  };

  // Delete handler with only new dialog confirm box
  // const handleDeleteConfirm = async () => {
  //   if (!chatToDelete) return;

  //   try {
  //     const config = {
  //       headers: {
  //         "Content-type": "application/json",
  //         Authorization: `Bearer ${user.token}`,
  //       },
  //     };

  //     await axios.delete("/api/chat/delete", {
  //       headers: config.headers,
  //       data: { chatId: chatToDelete._id },
  //     });

  //     setChats(chats.filter((c) => c._id !== chatToDelete._id));

  //     if (selectedChat?._id === chatToDelete._id) {
  //       setSelectedChat(null);
  //     }

  //     toast({
  //       title: "Chat Deleted Successfully",
  //       status: "success",
  //       duration: 3000,
  //       isClosable: true,
  //       position: "top",
  //     });
  //   } catch (error) {
  //     toast({
  //       title: "Error Deleting Chat",
  //       description: error.response?.data?.message || "Something went wrong",
  //       status: "error",
  //       duration: 3000,
  //       isClosable: true,
  //       position: "top",
  //     });
  //   } finally {
  //     // NEW: Clean up states
  //     setIsDeleteAlertOpen(false);
  //     setChatToDelete(null);
  //   }
  // };

  // Old Delete handler -> working fine with windows default alert
  // const handleDeleteChat = async (chatId) => {
  //   // Show confirmation dialog
  //   if (window.confirm("Chat will be deleted permanently. Are you sure?")) {
  //     try {
  //       const config = {
  //         headers: {
  //           "Content-type": "application/json",
