import {
  Button,
  FormControl,
  Input,
  Modal,
  ModalBody,
  ModalCloseButton,
  ModalContent,
  ModalFooter,
  ModalHeader,
  ModalOverlay,
  useDisclosure,
  useToast,
} from "@chakra-ui/react";
import React, { useState } from "react";
import { ChatState } from "../../Context/ChatProvider";
import axios from "axios";

const GroupChatModal = ({ children }) => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [groupChatName, setgroupChatName] = useState();
  const [setselectUsers, setSetselectUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [searchResult, setSearchResult] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const toast = useToast();

  const { user, chats, setChats } = ChatState();

  // handle to search users
  const handleSearch = async (query) => {
    setSearch(query);
    if (!query) {
      return;
    }
    try {
      setIsLoading(true);
      const config = {
        headers: {
          Authorization: `Bearer ${user.token}`,
        },
      };
      const { data } = await axios.get(`/api/user?search=${search}`, config);
      console.log(data);
      setIsLoading(false);
      setSearchResult(data);
    } catch (error) {
      toast({
        title: "Error Occured!",
        description: "Failed to load the search results",
        status: "error",
        duration: 3000,
        isClosable: true,
        position: "top-left",
      });
    }
  };

  // submit handle to create a group chat
  const handleSubmit = async () => {
    // if (!groupChatName || !setselectUsers) {
    //   toast({
    //     title: "Please fill all the fields",
    //     status: "warning",
    //     duration: 5000,
    //     isClosable: true,
    //     position: "top",
    //   });
    //   return;
    // }

    // try {
    //   const config = {
    //     headers: {
    //       Authorization: `Bearer ${user.token}`,
    //     },
    //   };
    //   const { data } = await axios.post(
    //     `/api/chat/group`,
    //     {
    //       name: groupChatName,
    //       users: JSON.stringify(setselectUsers.map((u) => u._id)),
    //     },
    //     config
    //   );
    //   setChats([data, ...chats]);
    //   onClose();
    //   toast({
    //     title: "New Group Chat Created!",
    //     status: "success",
    //     duration: 5000,
    //     isClosable: true,
    //     position: "bottom",
    //   });
    // } catch (error) {
    //   toast({
    //     title: "Error Occured!",
    //     description: "Failed to Create the Group Chat!",
    //     status: "error",
    //     duration: 5000,
    //     isClosable: true,
    //     position: "bottom",
    //   });
    // }
  };

  return (
    <>
      <span onClick={onOpen}>{children}</span>

      <Modal isOpen={isOpen} onClose={onClose}>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader
            fontSize="35px"
            fontFamily="Helvetica"
            display="flex"
            justifyContent="center"
          >
            Create Group Chat
          </ModalHeader>
          <ModalCloseButton />
          <ModalBody display="flex" flexDir="column" alignItems="center">
            {/* Chat Name */}
            <FormControl>
              <Input
                placeholder="Group Name"
                marginBottom={1}
                onChange={(e) => setgroupChatName(e.target.value)}
              ></Input>
            </FormControl>

            {/* Search */}
            <FormControl>
              <Input
                placeholder="Add Users to Group"
                marginBottom={3}
                onChange={(e) => handleSearch(e.target.value)}
              ></Input>
            </FormControl>

            {/* list of selected users */}
            {/* render searched users */}
          </ModalBody>

          <ModalFooter>
            <Button colorScheme="cyan" mr={2} onClick={handleSubmit}>
              Create Group
            </Button>
          </ModalFooter>
        </ModalContent>
      </Modal>
    </>
  );
};

export default GroupChatModal;
